      rename => { "session" => "[logx][cisco_asa][session]" }

      #New fields from ASA-113004
      rename => { "aaa_type" => "[logx][cisco_asa][aaa_type]" }

      #New fields from ASA-113009
      rename => { "policy" => "[logx][cisco_asa][policy]" }

      #New fields from ASA-113013
